CREATE OR REPLACE FUNCTION obter_se_nf_ordem_ou_contrato (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


/*Returno
N	Nota Normal
C	Nota de Contrato
O	Nota de Ordem de compra
*/
qt_existe_w		bigint;
ie_tipo_w			varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	nota_fiscal_item
where	(nr_ordem_compra IS NOT NULL AND nr_ordem_compra::text <> '')
and	nr_sequencia = nr_sequencia_p;

if (qt_existe_w > 0) then
	ie_tipo_w := 'O';
else
	select	count(*)
	into STRICT	qt_existe_w
	from	nota_fiscal_item
	where	(nr_contrato IS NOT NULL AND nr_contrato::text <> '')
	and	nr_sequencia = nr_sequencia_p;

	if (qt_existe_w > 0) then
		ie_tipo_w := 'C';
	end if;
end if;

return	ie_tipo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
