SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_nf_retem_iss (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01);
ie_retem_iss_w		varchar(01);
cd_estabelecimento_w	smallint;
cd_convenio_w		integer;
cd_cgc_w		varchar(14);
cd_operacao_nf_w		smallint;


BEGIN

select	cd_estabelecimento,
	obter_convenio_nf(nr_sequencia),
	cd_cgc,
	cd_operacao_nf
into STRICT	cd_estabelecimento_w,
	cd_convenio_w,
	cd_cgc_w,
	cd_operacao_nf_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

if (coalesce(cd_convenio_w,0) > 0) then
	select	coalesce(max(ie_retem_iss),'N')
	into STRICT	ie_retem_iss_w
	from	convenio_estabelecimento
	where	cd_convenio 		= cd_convenio_w
	and	cd_estabelecimento 	= cd_estabelecimento_w;
elsif (coalesce(cd_convenio_w,0) = 0) then
	select	coalesce(max(ie_retem_iss),'N')
	into STRICT	ie_retem_iss_w
	from	pessoa_juridica_estab
	where	cd_cgc			= cd_cgc_w
	and	cd_estabelecimento 	= cd_estabelecimento_w;
end if;

if (ie_retem_iss_w = 'N') then
	begin
	select	coalesce(max(ie_retem_iss), 'N')
	into STRICT	ie_retem_iss_w
	from	operacao_nota
	where	cd_operacao_nf	= cd_operacao_nf_w;

	if (ie_retem_iss_w = 'N') then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	nota_fiscal_trib a,
			tributo c
		where	a.cd_tributo 	 = c.cd_tributo
		and	c.ie_tipo_tributo	= 'ISS'
		and 	coalesce(c.ie_soma_diminui,'S') <> 'N'
		and 	a.nr_sequencia  	= nr_sequencia_p;
	end if;
	end;
end if;


if (ie_retem_iss_w = 'X') then
	ds_retorno_w	:= 'N';
end if;

if (ie_retem_iss_w = 'S') then
	ds_retorno_w	:= 'S';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
