SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_nf_ret_iss (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01);
ie_retem_iss_w		varchar(01);
cd_estabelecimento_w	smallint;
cd_convenio_w		integer;
cd_cgc_w		varchar(14);
cd_operacao_nf_w	smallint;
qt_registro_w		bigint;


BEGIN

select	cd_estabelecimento,
	obter_convenio_nf(nr_sequencia),
	cd_cgc,
	cd_operacao_nf
into STRICT	cd_estabelecimento_w,
	cd_convenio_w,
	cd_cgc_w,
	cd_operacao_nf_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

if (coalesce(cd_convenio_w,0) > 0) then
	begin
	
	select	coalesce(max(ie_retem_iss),'N')
	into STRICT	ie_retem_iss_w
	from	convenio_estabelecimento
	where	cd_convenio 		= cd_convenio_w
	and	cd_estabelecimento 	= cd_estabelecimento_w;
	
	end;
elsif (coalesce(cd_convenio_w,0) = 0) and (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
	begin
	
	select	coalesce(max(ie_retem_iss),'N')
	into STRICT	ie_retem_iss_w
	from	pessoa_juridica_estab
	where	cd_cgc			= cd_cgc_w
	and	cd_estabelecimento 	= cd_estabelecimento_w;
	
	end;
elsif (coalesce(cd_convenio_w,0) = 0) and (coalesce(cd_cgc_w::text, '') = '') then
	begin
	
	select	count(1)
	into STRICT	qt_registro_w
	from	nota_fiscal_trib a,
		tributo c 
	where	a.cd_tributo 	 	= c.cd_tributo 
	and	c.ie_tipo_tributo	= 'ISS' 
	and 	a.nr_sequencia  	= nr_sequencia_p;
		
	if (qt_registro_w > 0) then
		begin
		
		select	CASE WHEN coalesce(max(a.ie_retencao),'N')='N' THEN 'X'  ELSE 'S' END 
		into STRICT	ie_retem_iss_w
		from	nota_fiscal_trib a, 
			    tributo c 
		where	a.cd_tributo 	 	= c.cd_tributo 
		and	  c.ie_tipo_tributo	= 'ISS' 
		and 	a.nr_sequencia  	= nr_sequencia_p;
	
		end;
	else
		begin
		
		ie_retem_iss_w := 'X';
		
		end;
	end if;
		
	end;
end if;	

if (ie_retem_iss_w = 'N')then
	begin
	select	coalesce(max(ie_retem_iss), 'N')
	into STRICT	ie_retem_iss_w
	from	operacao_nota
	where	cd_operacao_nf	= cd_operacao_nf_w;

	if (ie_retem_iss_w = 'N') then
		
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	nota_fiscal_trib a, 
			tributo c 
		where	a.cd_tributo 	 = c.cd_tributo 
		and	  c.ie_tipo_tributo	= 'ISS' 
    and   coalesce(a.ie_retencao, 'X') <> 'N'
		and 	a.nr_sequencia  	= nr_sequencia_p;
	end if;
	
	end;
end if;


if (ie_retem_iss_w = 'X') then
	ds_retorno_w	:= 'N';
end if;

if (ie_retem_iss_w = 'S') then
	ds_retorno_w	:= 'S';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
