SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_nf_transferencia ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_transferencia_w			varchar(1);
nr_ordem_compra_w		bigint;
ie_tipo_ordem_w			varchar(1);


BEGIN

select	coalesce(max(nr_ordem_compra),0)
into STRICT	nr_ordem_compra_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

if (nr_ordem_compra_w = 0) then
	ie_transferencia_w := 'N';
else

	select	ie_tipo_ordem
	into STRICT	ie_tipo_ordem_w
	from	ordem_compra
	where	nr_ordem_compra = nr_ordem_compra_w;

	if (ie_tipo_ordem_w = 'T') then
		ie_transferencia_w := 'S';
	else
		ie_transferencia_w := 'N';
	end if;
end if;

return	ie_transferencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
