SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_nivel_atencao_setor (cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

					
ds_retorno_w				varchar(1) := 'S';
ie_nivel_atencao_usuario_w	varchar(1) := 'T';
cd_classif_setor_w			varchar(2);
ie_consistir_w				varchar(1);
					

BEGIN
if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '')  then
	
	ie_nivel_atencao_usuario_w := wheb_assist_pck.get_nivel_atencao_perfil;
	
	if ( ie_nivel_atencao_usuario_w = 'P' ) then
	
		ie_consistir_w := obter_param_usuario(916, 1205, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_consistir_w);
		
		if (coalesce(ie_consistir_w,'S') = 'S') then
	
			Select  max(cd_classif_setor)
			into STRICT	cd_classif_setor_w
			from	setor_atendimento
			where	cd_setor_atendimento = cd_setor_atendimento_p;
		
			if (coalesce(cd_classif_setor_w,1) <> 13) then
			
				ds_retorno_w := 'N';
			
			end if;
			
		end if;
	
	end if;
	
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
