CREATE OR REPLACE FUNCTION obter_se_nota_conta_protocolo ( nr_seq_protocolo_p bigint, nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_nota_fiscal_w	varchar(1) := 'N';

c01 CURSOR FOR 
SELECT	coalesce(max('S'),'N') 
from	nota_fiscal 
where (nr_seq_protocolo_p 	<> 0) 
and	ie_situacao = '1' 
and (nr_seq_protocolo 	= nr_seq_protocolo_p) 
and (nr_interno_conta_p	= nr_interno_conta or 
	nr_interno_conta_p	= 0) 

union all
 
SELECT	coalesce(max('S'),'N') 
from	nota_fiscal b, 
	conta_paciente a 
where (nr_seq_protocolo_p <> 0) 
and	a.nr_seq_protocolo	= nr_seq_protocolo_p 
and	a.nr_interno_conta	= b.nr_interno_conta 
and	b.ie_situacao = '1' 
and (nr_interno_conta_p	= a.nr_interno_conta or 
	nr_interno_conta_p	= 0) 

union
 
select	coalesce(max('S'),'N') 
from	titulo_receber 
where	nr_seq_protocolo	= nr_seq_protocolo_p 
and 	coalesce(nr_nota_fiscal,'0')	<> '0' 
and (nr_interno_conta_p	= nr_interno_conta or 
	nr_interno_conta_p	= 0) 
order by 1;


BEGIN 
open c01;
loop 
fetch c01 into 
	ie_nota_fiscal_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_nota_fiscal_w	:= ie_nota_fiscal_w;
end loop;
close c01;
 
return substr(ie_nota_fiscal_w,1,1);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
