CREATE OR REPLACE FUNCTION obter_se_nota_entrada_nfe (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_entrada_saida_w varchar(3);
ie_recusa_w        operacao_nota.ie_recusa%type;
ie_nota_recusa_w   operacao_nota.ie_nota_recusa%type;


BEGIN

select coalesce(obter_se_nota_entrada_saida(a.nr_sequencia),'S'),
       coalesce(b.ie_recusa,'N'),
       coalesce(b.ie_nota_recusa,'N')
into STRICT   ie_entrada_saida_w,
       ie_recusa_w,
       ie_nota_recusa_w
from   nota_fiscal a,
       operacao_nota b
where  a.nr_sequencia = nr_sequencia_p
and    a.cd_operacao_nf = b.cd_operacao_nf;

if ((ie_entrada_saida_w = 'E') and (ie_recusa_w = 'S') and (ie_nota_recusa_w = 'N')) then
    return 'S';
else
    return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
