CREATE OR REPLACE FUNCTION obter_se_nota_entrada_saida (nr_seq_nota_p bigint, ie_exibir_erro_p text default 'N') RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(01);
cd_operacao_estoque_w		smallint;
cd_operacao_nota_w		smallint;


BEGIN

select	cd_operacao_nf
into STRICT	cd_operacao_nota_w
from	nota_fiscal
where	nr_sequencia		= nr_seq_nota_p;


select	coalesce(max(cd_operacao_estoque),0)
into STRICT	cd_operacao_estoque_w
from	operacao_nota
where	cd_operacao_nf		= cd_operacao_nota_w;

if (cd_operacao_estoque_w = 0) and (ie_exibir_erro_p = 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265532);
	--'A operação da nota não está vinculada com uma operação de estoque.'
end if;

select	ie_entrada_saida
into STRICT	ie_retorno_w
from	operacao_estoque
where	cd_operacao_estoque	= cd_operacao_estoque_w;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
