CREATE OR REPLACE FUNCTION obter_se_nota_item_me ( nr_seq_nota_p bigint) RETURNS varchar AS $body$
DECLARE


nr_ordem_compra_w		bigint;
qt_existe_w			integer;
ie_integracao_me_w		varchar(1) := 'N';

c01 CURSOR FOR
	SELECT	nr_ordem_compra
	from	nota_fiscal_item
	where	nr_sequencia = nr_seq_nota_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_ordem_compra_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	ordem_compra
	where	ie_origem_imp = 'MercadoEletronico'
	and	nr_ordem_compra = nr_ordem_compra_w;

	if (qt_existe_w > 0) then
		ie_integracao_me_w := 'S';
		goto final;
	end if;

	end;
end loop;
close c01;

<<final>>

return	ie_integracao_me_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
