CREATE OR REPLACE FUNCTION obter_se_nota_servico (nr_titulo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

select 	coalesce(o.ie_servico,'N')
into STRICT	ds_retorno_w
from 	nota_fiscal n,
	titulo_pagar p,
	operacao_nota o
where	n.nr_sequencia = p.nr_seq_nota_fiscal
and	n.cd_operacao_nf = o.cd_operacao_nf
