CREATE OR REPLACE FUNCTION obter_se_nota_tit_abertos ( nr_sequencia_p nota_fiscal.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_existe_w	varchar(1) := 'N';


BEGIN

select 	coalesce(max('S'),'N')
into STRICT	ie_existe_w
from	nota_fiscal n
where 	n.nr_sequencia = nr_sequencia_p
and	exists (SELECT 	1
		from	titulo_pagar p
		where	p.nr_seq_nota_fiscal = n.nr_sequencia
		and	p.ie_situacao = 'A'
		
union

		SELECT 	1
		from	titulo_receber r
		where	r.nr_seq_nf_saida = n.nr_sequencia
		and	r.ie_situacao = 1);
		
return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
