SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_notificacao_titulo ( nr_titulo_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

							  
ds_retorno_w	varchar(255);

/*IE_OPCAO_P 
1 = Verifica se existem notificações para o título 
2 = Retorna se foi impresso ou nao 
*/
							  
 

BEGIN 
 
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
 
	if (ie_opcao_p = 1) then 
 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_retorno_w 
		from  pls_notificacao_pagador a, 
				pls_notificacao_item b, 
				pls_notificacao_lote c, 
				pls_notificacao_regra d, 
				pls_notif_regra_item e, 
				titulo_receber f 		 
		where  a.nr_sequencia = b.nr_seq_notific_pagador 
		and	 	a.nr_seq_lote = c.nr_sequencia 
		and	 	c.nr_seq_regra = d.nr_sequencia 
		and	 	d.nr_sequencia = e.nr_seq_regra 
		and	 	e.ie_tipo_item in (15, 1) 
		and	 	b.nr_titulo   = f.nr_titulo 
		and	 	b.nr_titulo   = nr_titulo_p 
		and		coalesce(f.ie_negociacao_impressa,'N') = 'N';
		 
	elsif (ie_opcao_p = 2) then 
	 
		select	substr(coalesce(max(a.ie_negociacao_impressa),'N'),1,1) 
		into STRICT	ds_retorno_w 
		from	titulo_receber a 
		where	a.nr_titulo = nr_titulo_p;
	 
	end if;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_notificacao_titulo ( nr_titulo_p bigint, ie_opcao_p bigint) FROM PUBLIC;

