CREATE OR REPLACE FUNCTION obter_se_novos_itens_gpt (nr_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_lib_farm_p text, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, qt_horas_quimio_p bigint default 0) RETURNS varchar AS $body$
DECLARE

			
/*
S - SIM
N - NAO
I - Contem intervencao farmacia
*/
ie_tipo_usuario_w		varchar(1);
ds_retorno 				varchar(1) := 'N';
dt_inicio_analise_w		gpt_hist_analise_plano.dt_inicio_analise%type;
dt_fim_analise_w		gpt_hist_analise_plano.dt_fim_analise%type;
dt_prescr				timestamp;
ie_prescr_farm_w		prescr_medica.ie_prescr_farm%type;
nr_prescricao_w			prescr_medica.nr_prescricao%type;

C01 CURSOR FOR
	SELECT	coalesce(a.ie_prescr_farm,'N'),
			a.nr_prescricao
	from	prescr_medica a
	where	((a.nr_atendimento = nr_atendimento_p) or (a.cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(a.nr_atendimento::text, '') = ''))
	and (ie_lib_farm_p = 'N' or coalesce(a.ie_lib_farm,'N') = 'S')
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_liberacao_farmacia::text, '') = ''
	and		coalesce(a.dt_inicio_analise_farm::text, '') = ''
	and		coalesce(a.nm_usuario_analise_farm::text, '') = ''
	and		((coalesce(a.nr_seq_atend::text, '') = '' and a.dt_prescricao >= dt_inicio_analise_w) or (coalesce(a.nr_seq_atend::text, '') = '' and ie_tipo_usuario_w = 'F' and a.dt_liberacao >= dt_inicio_analise_w and coalesce(dt_fim_analise_w::text, '') = '') or
			((a.nr_seq_atend IS NOT NULL AND a.nr_seq_atend::text <> '') and a.dt_prescricao between inicio_dia(clock_timestamp() - interval '3 days') and clock_timestamp() + (qt_horas_quimio_p / 24)));


BEGIN
CALL wheb_assist_pck.set_informacoes_usuario(cd_estabelecimento_p, obter_perfil_ativo, nm_usuario_p);
ie_tipo_usuario_w := wheb_assist_pck.obterparametrofuncao(252,1);

if (ie_tipo_usuario_w = 'E') then
	
	dt_inicio_analise_w	:= obter_dt_analise_gpt(nr_atendimento_p, null, null, 'E', 'DTI', ie_lib_farm_p, cd_pessoa_fisica_p);
	
	select	coalesce(max('S'),'N')
	into STRICT 	ds_retorno
	from	prescr_medica a,
			prescr_medica_compl b
	where 	a.nr_prescricao = b.nr_prescricao
	and		((a.nr_atendimento = nr_atendimento_p) or (a.cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(a.nr_atendimento::text, '') = ''))
	--and 	dt_revisao 	is null
	and		coalesce(b.dt_inicio_analise_enf::text, '') = ''
	and 	coalesce(a.dt_liberacao::text, '') = ''
	--and 	nm_usuario_revisao 	is null
	and		coalesce(b.nm_usuario_analise_enf::text, '') = ''
	and 	a.cd_funcao_origem 	not in (900,924,950)
	and		(a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '')
	and 	a.dt_prescricao >= dt_inicio_analise_w;
	
elsif (ie_tipo_usuario_w = 'F') then

	dt_inicio_analise_w	:= obter_dt_analise_gpt(nr_atendimento_p, null, null, 'F', 'DTI', ie_lib_farm_p, cd_pessoa_fisica_p);
	dt_fim_analise_w := obter_dt_analise_gpt(nr_atendimento_p, null, null, 'F', 'DTF', ie_lib_farm_p, cd_pessoa_fisica_p);

	open c01;
	loop
	fetch c01 into	
		ie_prescr_farm_w,
		nr_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (ie_prescr_farm_w = 'N') then
			return 'S';
		else
			ds_retorno := 'I';
		end if;
		end;
	end loop;
	close c01;
end if;

return ds_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_novos_itens_gpt (nr_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_lib_farm_p text, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, qt_horas_quimio_p bigint default 0) FROM PUBLIC;

