CREATE OR REPLACE FUNCTION obter_se_npt_acm (nr_prescricao_p bigint, nr_seq_npt_p bigint) RETURNS varchar AS $body$
DECLARE


ie_acm_sn_w	varchar(1) := 'N';
ie_acm_w	varchar(1);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_npt_p IS NOT NULL AND nr_seq_npt_p::text <> '') then
	begin
	select	coalesce(max(ie_acm),'N')
	into STRICT	ie_acm_w
	from	nut_pac
	where	nr_prescricao	= nr_prescricao_p
	and		nr_sequencia	= nr_seq_npt_p;

	if (ie_acm_w = 'S') then
		begin
		ie_acm_sn_w	:= 'S';
		end;
	end if;
	end;
end if;

return ie_acm_sn_w;

end;
$body$
LANGUAGE PLPGSQL
