CREATE OR REPLACE FUNCTION obter_se_npt_disp (nr_seq_protocolo_p protocolo_npt.nr_sequencia%type, cd_material_p material.cd_material%type) RETURNS varchar AS $body$
DECLARE


ie_gera_dispensacao_w	protocolo_npt.ie_gera_dispensacao%type;
ie_dispensacao_w		protocolo_npt_prod.ie_dispensacao%type;
			

BEGIN

ie_gera_dispensacao_w	:= 'D';

if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then
	select	coalesce(max(a.ie_gera_dispensacao),'N')
	into STRICT	ie_gera_dispensacao_w
	from	protocolo_npt	a
	where	a.nr_sequencia	= nr_seq_protocolo_p;
	
	if (ie_gera_dispensacao_w <> 'N' and (cd_material_p IS NOT NULL AND cd_material_p::text <> '')) then
		select	coalesce(max(ie_dispensacao),'S')
		into STRICT	ie_dispensacao_w
		from	protocolo_npt_prod	a
		where	a.nr_seq_protocolo 	= nr_seq_protocolo_p
		and		a.cd_material 	 	= cd_material_p;
		
		if (ie_dispensacao_w = 'T') then
			ie_gera_dispensacao_w := 'S';
		elsif (ie_dispensacao_w = 'N') then
			ie_gera_dispensacao_w := ie_dispensacao_w;
		end if;
	end if;
end if;

return	ie_gera_dispensacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
