SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_npt_protocolo (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_nut_pac_w	bigint;
qt_produtos_w		integer;

ds_retorno_w		varchar(1) := 'N';


BEGIN

select 	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_nut_pac_w
from	nut_pac
where	nr_prescricao 	= nr_prescricao_p
and	ie_forma	= 'P'
and	ie_npt_adulta 	= 'S';

if (nr_seq_nut_pac_w <> 0) then
	select	coalesce(count(*),0)
	into STRICT	qt_produtos_w
	from	nut_pac_elem_mat
	where	coalesce(qt_protocolo,0)	<> qt_volume
	and	nr_seq_nut_pac 	= nr_seq_nut_pac_w;

	if (qt_produtos_w = 0) then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_npt_protocolo (nr_prescricao_p bigint) FROM PUBLIC;

