CREATE OR REPLACE FUNCTION obter_se_obito (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);


BEGIN
if (CD_PESSOA_FISICA_P IS NOT NULL AND CD_PESSOA_FISICA_P::text <> '') then
	select  CASE WHEN dt_obito='' THEN 'N'  ELSE 'S' END
	into STRICT 	ds_retorno_w
	from 	pessoa_fisica
	where   CD_PESSOA_FISICA = CD_PESSOA_FISICA_P;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
