CREATE OR REPLACE FUNCTION obter_se_objetivo_regra ( cd_material_p bigint, ie_objetivo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
qt_registro_w		bigint;

BEGIN
/*  Verifica se existe algum registro na tabela para aquele material */

select	count(*)
into STRICT		qt_registro_w
from		rep_objetivo_medicamento
where	cd_material = cd_material_p;
/* Se houver verifica se tem algum registro para aquele objetivo de uso */

if (qt_registro_w > 0) then
	/* Verifica se o objetivo de uso esta cadastrado para aquele medicamento */

	select	count(*)
	into STRICT		qt_registro_w
	from		rep_objetivo_medicamento
	where	cd_material = cd_material_p
	and		ie_objetivo = ie_objetivo_p;
	/* Define os valores de retorno */

	if (qt_registro_w = 0) then
		ds_retorno_w := 'N';
	end if;
end if;
/* Retorna o valor  */

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
