CREATE OR REPLACE FUNCTION obter_se_obriga_alta_proc ( cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


cd_area_proced_w	area_procedimento.cd_area_procedimento%type;
cd_espec_proced_w	especialidade_proc.cd_especialidade%type;
cd_grupo_proced_w	grupo_proc.cd_grupo_proc%type;
cd_subgrupo_w		sus_subgrupo.cd_subgrupo%type;
cd_forma_organizacao_w	sus_forma_organizacao.cd_forma_organizacao%type;
cd_grupo_w		sus_grupo.cd_grupo%type;

ie_permite_alta_w	varchar(1) := 'N';
C01 CURSOR FOR
	SELECT	ie_permite_alta
	from	regra_proced_alta
	where	((coalesce(cd_procedimento::text, '') = '') or (coalesce(cd_procedimento, cd_procedimento_p)	= cd_procedimento_p
		and (coalesce(ie_origem_proced, ie_origem_proced_p)	= ie_origem_proced_p)))	
	and	coalesce(cd_area_procedimento, cd_area_proced_w)	= cd_area_proced_w
	and	coalesce(cd_especialidade, cd_espec_proced_w)	= cd_espec_proced_w
	and	coalesce(cd_grupo_proc, cd_grupo_proced_w)		= cd_grupo_proced_w
	and	((coalesce(cd_forma_organizacao::text, '') = '') or (cd_forma_organizacao = cd_forma_organizacao_w))
	and	((coalesce(cd_subgrupo::text, '') = '') or (cd_subgrupo = cd_subgrupo_w))
	and	((coalesce(cd_grupo_sus::text, '') = '') or (cd_grupo_sus = cd_grupo_w))
	order by  coalesce(cd_procedimento,0),
		  coalesce(cd_grupo_proc,0), 
		  coalesce(cd_especialidade,0), 
		  coalesce(cd_area_procedimento,0), 
		  coalesce(cd_subgrupo,0), 
		  coalesce(cd_grupo_sus,0);


BEGIN

select	coalesce(max(cd_area_procedimento),0),
	coalesce(max(cd_especialidade),0),
	coalesce(max(cd_grupo_proc),0)
into STRICT	cd_area_proced_w,
	cd_espec_proced_w,
	cd_grupo_proced_w
from	estrutura_procedimento_v
where	cd_procedimento = cd_procedimento_p
and	ie_origem_proced = ie_origem_proced_p;

select	max(Sus_Obter_Estrut_Proc(cd_procedimento_p, ie_origem_proced_p, 'C', 'F')),
	max(Sus_Obter_Estrut_Proc(cd_procedimento_p, ie_origem_proced_p, 'C', 'S')),
	max(Sus_Obter_Estrut_Proc(cd_procedimento_p, ie_origem_proced_p, 'C', 'G'))
into STRICT	cd_forma_organizacao_w,
	cd_subgrupo_w,
	cd_grupo_w
;

open C01;
loop
fetch C01 into	
	ie_permite_alta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_permite_alta_w := ie_permite_alta_w;
	end;
end loop;
close C01;


return	ie_permite_alta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
