CREATE OR REPLACE FUNCTION obter_se_obriga_campo_item ( cd_material_p bigint, cd_procedimento_p bigint, cd_recomendacao_p bigint) RETURNS char AS $body$
DECLARE


ie_retorno_w	char(1);
ds_sql_w		varchar(500);

BEGIN

ds_sql_w	:=
	' select	coalesce(max(ie_obrigar_justif),''N'') ' ||
	' from		adep_obrigar_campo_adm ' ||
	' where		rownum = 1 ';
	
if (coalesce(cd_material_p,0) > 0) then
	ds_sql_w	:=	ds_sql_w ||
					' and	((coalesce(cd_material,0) = ' || cd_material_p || ' ' ||
					' and	coalesce(cd_procedimento,cd_recomendacao,0) = 0) 
					or cd_material is null and cd_procedimento is null and cd_recomendacao is null)';				
elsif (coalesce(cd_procedimento_p,0) > 0) then
	ds_sql_w	:=	ds_sql_w ||
					' and	coalesce(cd_procedimento,0) = ' || cd_procedimento_p || ' ' ||
					' and	coalesce(cd_recomendacao,cd_material,0) = 0 ';	
elsif (coalesce(cd_recomendacao_p,0) > 0) then
	ds_sql_w	:=	ds_sql_w ||
					' and	coalesce(cd_recomendacao,0) = ' || cd_recomendacao_p || ' ' ||
					' and	coalesce(cd_material,cd_procedimento,0) = 0 ';
else
	ds_sql_w	:=	ds_sql_w || ' and	coalesce(cd_material,cd_procedimento,cd_recomendacao,0) = 0 ';
end if;

EXECUTE ds_sql_w into STRICT ie_retorno_w;

return	ie_retorno_w;

end;
