CREATE OR REPLACE FUNCTION obter_se_obriga_cid (cd_material_p bigint, ie_objetivo_uso_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE



ie_controle_medico_w	smallint;
ie_exige_cid_w			varchar(1)	:= 'N';
cd_perfil_w				integer;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	cd_perfil_w	:= obter_perfil_ativo;

	select	max(obter_controle_medic(cd_material,cd_estabelecimento_p,ie_controle_medico,null,null,null))
	into STRICT		ie_controle_medico_w
	from		material
	where	cd_material		=	cd_material_p;

	select	coalesce(max('S'),'N')
	into STRICT		ie_exige_cid_w
	from		regra_antimicrobiano
	where	cd_regra			= ie_controle_medico_w
	and		coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w
	and		cd_estabelecimento		= cd_estabelecimento_p
	and		((ie_exige_cid = 'S') or
			 (ie_exige_cid = 'P' AND ie_objetivo_uso_p = 'F') or
			 (ie_exige_cid = 'C' AND ie_objetivo_uso_p = 'C') or
			 (ie_exige_cid = 'T' AND ie_objetivo_uso_p = 'T') or
			 (ie_exige_cid = 'D' AND ie_objetivo_uso_p = 'D') or
			 (ie_exige_cid = 'E' AND ie_objetivo_uso_p = 'E'))
	order 	by coalesce(cd_perfil,0),
			nr_sequencia desc;

	return	ie_exige_cid_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
