SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_obriga_clearance ( nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_material_w		varchar(250);

--ie_opcao = O
--ie_opcao = Q
C01 CURSOR FOR
	SELECT	distinct substr(b.ds_material,1,240)
	from	prescr_material a,
			material b
	where	a.nr_prescricao = nr_prescricao_p
	and		a.cd_material = b.cd_material
	and		coalesce(b.ie_clearance, 'N') = 'S';


BEGIN
if (ie_opcao_p = 'O') then

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	prescr_material a,
			material b where		a.cd_material = b.cd_material
	and		a.nr_prescricao = nr_prescricao_p
	and		coalesce(b.ie_clearance,'N') = 'S' LIMIT 1;

	if (ds_retorno_w = 'S') then

		select	coalesce(max('N'),'S')
		into STRICT	ds_retorno_w
		from	prescr_medica a,
				pac_clereance_creatinina b where		a.cd_pessoa_fisica = b.cd_pessoa_fisica
		and		a.nr_prescricao = nr_prescricao_p LIMIT 1;

	end if;

elsif (ie_opcao_p = 'Q') then

	open C01;
	loop
	fetch C01 into
		ds_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_retorno_w := substr(ds_retorno_w ||chr(10)||' - '||ds_material_w,1,4000);
		end;
	end loop;
	close C01;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
