CREATE OR REPLACE FUNCTION obter_se_obriga_compl_pf ( cd_estabelecimento_p bigint, cd_perfil_p bigint, ie_tipo_complemento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_obriga_w		varchar(100);
qt_idade_w		integer;


BEGIN

select	substr(obter_idade(dt_nascimento,clock_timestamp(),'A'),1,5)
into STRICT	qt_idade_w
from	pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

begin
select	x.ie_obriga
into STRICT	ie_obriga_w
from	(SELECT	ie_obriga
	from	regra_compl_pessoa_fisica
	where	cd_estabelecimento = cd_estabelecimento_p
	and	coalesce(cd_perfil,coalesce(cd_perfil_p,0)) = coalesce(cd_perfil_p,0)
	and	((coalesce(qt_idade_minima::text, '') = '' or coalesce(qt_idade_maxima::text, '') = '') or (qt_idade_w between qt_idade_minima and qt_idade_maxima))
	and	ie_tipo_complemento = ie_tipo_complemento_p
	order by cd_perfil,
		cd_setor_atendimento desc,
		coalesce(qt_idade_minima,0) desc,
		coalesce(qt_idade_maxima,99999)) x LIMIT 1;
exception
when others then
	ie_obriga_w := 'N';
end;

return	ie_obriga_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
