CREATE OR REPLACE FUNCTION obter_se_obriga_justificativa (cd_material_p bigint, qt_peso_p bigint, cd_setor_prescr_p bigint, cd_convenio_p bigint, ie_disp_infusao_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w
from	regra_prescr_mat_justi
where	coalesce(cd_material, coalesce(cd_material_p,0)) = coalesce(cd_material_p,0)
and		coalesce(cd_setor_atendimento, coalesce(cd_setor_prescr_p,0)) = coalesce(cd_setor_prescr_p,0)
and		qt_peso_p between coalesce(qt_peso_inicial,0) and coalesce(qt_peso_final,999)
and		((coalesce(cd_perfil::text, '') = '') or (cd_perfil = obter_perfil_ativo))
and		((coalesce(cd_convenio::text, '') = '') or (cd_convenio = cd_convenio_p))
and 	((coalesce(ie_bomba_infusao::text, '') = '') or (ie_bomba_infusao =  ie_disp_infusao_p))
and		coalesce(ie_situacao, 'A') = 'A'
and		((coalesce(cd_doenca::text, '') = '') or (obter_se_atend_cid(cd_doenca, nr_atendimento_p) = 'S'));

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
