CREATE OR REPLACE FUNCTION obter_se_obriga_just_hemo (ie_aliquotado_p text, ie_filtrado_p text, ie_irradiado_p text, ie_lavado_p text, nr_seq_derivado_p bigint) RETURNS varchar AS $body$
DECLARE


ie_obriga_w		varchar(1);


BEGIN

select	coalesce(max(ie_obriga_justificativa),'N')
into STRICT	ie_obriga_w
from	rep_hemocomponente_justif
where	ie_aliquotado	= ie_aliquotado_p
and	ie_filtrado	= ie_filtrado_p
and	ie_irradiado	= ie_irradiado_p
and	ie_lavado	= ie_lavado_p
and	coalesce(nr_seq_derivado,nr_seq_derivado_p)	= nr_seq_derivado_p;

return	ie_obriga_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
