CREATE OR REPLACE FUNCTION obter_se_obriga_obs ( dt_alta_p timestamp, nr_seq_motivo_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_obriga_obs_w		varchar(1);
ie_obriga_obs_tempo_alta_w	varchar(1);
ds_retorno_w		varchar(1);		
			 

BEGIN 
 
if (dt_alta_p IS NOT NULL AND dt_alta_p::text <> '') then 
	if (nr_seq_motivo_p IS NOT NULL AND nr_seq_motivo_p::text <> '') then 
	 
		select	coalesce(CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END ,'N') 
		into STRICT	ie_obriga_obs_w 
		from	regra_tempo_gerar_alta 
		where	ie_situacao = 'A' 
		and	obter_min_entre_datas(dt_alta_p, clock_timestamp(), 1) > qt_tempo 
		and	nr_seq_motivo = nr_seq_motivo_p;
	 
	end if;
	 
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	 
		select	coalesce(CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END ,'N') 
		into STRICT	ie_obriga_obs_tempo_alta_w 
		from	atendimento_paciente a 
		where	a.nr_atendimento = nr_atendimento_p 
		and	obter_min_entre_datas(a.dt_alta_medico, dt_alta_p, 1) >= (	SELECT 	max(x.qt_tempo_alta) 
									from	setor_atendimento x 
									where	x.cd_setor_atendimento = obter_setor_atendimento(a.nr_atendimento));
	 
	end if;	
end if;
 
if (ie_obriga_obs_w = 'S') or (ie_obriga_obs_tempo_alta_w = 'S') then 
	ds_retorno_w := 'S';
else 
	ds_retorno_w := 'N';	
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
