CREATE OR REPLACE FUNCTION obter_se_obr_item_solic_parec ( nr_solic_compra_p bigint, nr_item_solic_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(01) := 'N';
cd_estabelecimento_w		smallint;
qt_existe_w			bigint;
cd_material_w			integer;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
ie_obriga_parecer_w		varchar(01);

C01 CURSOR FOR 
SELECT	ie_obriga_parecer 
from	regra_solic_compra_parecer 
where	cd_estabelecimento				 	= cd_estabelecimento_w 
and	ie_situacao					= 'A' 
and	coalesce(cd_material,cd_material_w) 			= cd_material_w 
and	coalesce(cd_grupo_material,cd_grupo_material_w)		= cd_grupo_material_w 
and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w)	= cd_subgrupo_material_w 
and	coalesce(cd_classe_material,cd_classe_material_w)		= cd_classe_material_w 
order by coalesce(cd_material,0), 
	coalesce(cd_classe_material,0), 
	coalesce(cd_subgrupo_material,0), 
	coalesce(cd_grupo_material,0);
	

BEGIN 
 
select	a.cd_estabelecimento, 
	b.cd_material 
into STRICT	cd_estabelecimento_w, 
	cd_material_w 
from	solic_compra_item b, 
	solic_compra a 
where	a.nr_solic_compra 		= b.nr_solic_compra 
and	b.nr_item_solic_compra	= nr_item_solic_compra_p 
and	a.nr_solic_compra		= nr_solic_compra_p;
 
select	count(*) 
into STRICT	qt_existe_w 
from	regra_solic_compra_parecer 
where	cd_estabelecimento 	= cd_estabelecimento_w 
and	ie_situacao		= 'A';
 
if (qt_existe_w > 0) then 
	select	cd_grupo_material, 
		cd_subgrupo_material, 
		cd_classe_material 
	into STRICT	cd_grupo_material_w, 
		cd_subgrupo_material_w, 
		cd_classe_material_w 
	from	estrutura_material_v 
	where	cd_material = cd_material_w;
 
	open C01;
	loop 
	fetch C01 into	 
		ie_obriga_parecer_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		ds_retorno_w 	:= coalesce(ie_obriga_parecer_w,'N');
		end;
	end loop;
	close C01;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
