CREATE OR REPLACE FUNCTION obter_se_oci_pend_entrega ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS varchar AS $body$
DECLARE


ie_pendente_w		varchar(1);
qt_pendente_w		double precision;
qt_original_w		double precision;


BEGIN
ie_pendente_w	:= 'N';

select	coalesce(sum(b.qt_prevista_entrega),0) - coalesce(sum(b.qt_real_entrega),0)
into STRICT	qt_pendente_w
from	ordem_compra_item_entrega b,
	ordem_compra_item a
where	a.nr_ordem_compra	= nr_ordem_compra_p
and	a.nr_item_oci		= nr_item_oci_p
and	a.nr_ordem_compra	= b.nr_ordem_compra
and	a.nr_item_oci		= b.nr_item_oci
and	coalesce(b.dt_cancelamento::text, '') = '';
if (qt_pendente_w > 0) then
	ie_pendente_w	:= 'S';
end if;

return	ie_pendente_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
