CREATE OR REPLACE FUNCTION obter_se_ocorrencia_liquidacao (cd_banco_p bigint, ds_erro_p text) RETURNS varchar AS $body$
DECLARE


ie_liquidacao_w		varchar(1)	:= 'N';


BEGIN

select	CASE WHEN position(max(a.cd_retorno_liq) in ds_erro_p)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_liquidacao_w
from	banco_retorno_cp a
where	a.cd_banco	= cd_banco_p;

RETURN ie_liquidacao_w;

END;
$body$
LANGUAGE PLPGSQL
