CREATE OR REPLACE FUNCTION obter_se_ocorrencia_rej ( cd_banco_p bigint, cd_ocorrencia_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'N';
ds_ocorrencia_um_w	varchar(2);
ds_ocorrencia_dois_w	varchar(2);
ds_ocorrencia_tres_w	varchar(2);
ds_ocorrencia_quatro_w	varchar(2);
ds_ocorrencia_cinco_w	varchar(2);



BEGIN

if (cd_ocorrencia_p IS NOT NULL AND cd_ocorrencia_p::text <> '') then
	ds_ocorrencia_um_w	:= substr(cd_ocorrencia_p,1,2);

	if (length(cd_ocorrencia_p) >= 4) then
		ds_ocorrencia_dois_w	:= substr(cd_ocorrencia_p,3,2);
	end if;

	if (length(cd_ocorrencia_p) >= 6) then
		ds_ocorrencia_tres_w	:= substr(cd_ocorrencia_p,5,2);
	end if;

	if (length(cd_ocorrencia_p) >= 8) then
		ds_ocorrencia_quatro_w	:= substr(cd_ocorrencia_p,7,2);
	end if;

	if (length(cd_ocorrencia_p) >= 10) then
		ds_ocorrencia_cinco_w	:= substr(cd_ocorrencia_p,9,2);
	end if;

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	banco_ocorr_escrit_ret
	where	cd_banco		= cd_banco_p
	and (ds_ocorrencia_um_w	= cd_ocorrencia
	or	ds_ocorrencia_dois_w	= cd_ocorrencia
	or	ds_ocorrencia_tres_w	= cd_ocorrencia
	or	ds_ocorrencia_quatro_w	= cd_ocorrencia
	or	ds_ocorrencia_cinco_w	= cd_ocorrencia)
	and	ie_rejeitado 		= 'S';

end if;



RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
