CREATE OR REPLACE FUNCTION obter_se_oculta_legenda (cd_perfil_p bigint, cd_estabelecimento_p bigint, nr_seq_legenda_p bigint) RETURNS varchar AS $body$
DECLARE


ie_ocultar_w	varchar(1);
ie_existe_regra_perfil_w varchar(1);


BEGIN

select 	coalesce(max('S'),'N')
into STRICT	ie_existe_regra_perfil_w
from	tasy_padrao_cor_perfil x
where 	x.nr_seq_padrao    = nr_seq_legenda_p
and	x.cd_estabelecimento  = cd_estabelecimento_p
and 	x.cd_perfil = cd_perfil_p;

ie_ocultar_w := 'N';

if (ie_existe_regra_perfil_w = 'S') then

	select 	coalesce(max(ie_ocultar),'N')
	into STRICT	ie_ocultar_w
	from	tasy_padrao_cor_perfil x
	where 	x.nr_seq_padrao    = nr_seq_legenda_p
	and	x.cd_estabelecimento  = cd_estabelecimento_p
	and 	x.cd_perfil = cd_perfil_p;

else
	select 	coalesce(max(ie_ocultar),'N')
	into STRICT	ie_ocultar_w
	from	tasy_padrao_cor_perfil x
	where 	x.nr_seq_padrao    = nr_seq_legenda_p
	and	x.cd_estabelecimento  = cd_estabelecimento_p
	and 	coalesce(x.cd_perfil::text, '') = '';

end if;

return ie_ocultar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
