CREATE OR REPLACE FUNCTION obter_se_oc_cancelado (nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
			 
ie_cancelado_w		varchar(1);
nr_seq_motivo_w		ordem_compra.nr_seq_motivo_cancel%type;


BEGIN 
 
select	coalesce(max(nr_seq_motivo_cancel), 0) 
into STRICT	nr_seq_motivo_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p;
 
if (nr_seq_motivo_w > 0) then 
	ie_cancelado_w := 'S';
else 
	ie_cancelado_w := 'N';
end if;
 
return	ie_cancelado_w;
 
end;
$body$
