CREATE OR REPLACE FUNCTION obter_se_oc_pend_cgc ( cd_cgc_fornecedor_p text, cd_estabelecimento_p bigint, ie_tipo_parametro_p text default 'OP') RETURNS bigint AS $body$
DECLARE

 
qt_reg_w	bigint := 0;


BEGIN 
if (ie_tipo_parametro_p = 'OP') then 
   select	count(*) 
   into STRICT	qt_reg_w 
   from	ordem_compra 
   where	cd_cgc_fornecedor = cd_cgc_fornecedor_p 
   and	cd_estabelecimento = cd_estabelecimento_p 
   and (ie_tipo_ordem <> 'G') 
   and (coalesce(dt_baixa::text, '') = '') 
   and (coalesce(dt_aprovacao::text, '') = '');
elsif (ie_tipo_parametro_p = 'OL') then 
   select	count(*) 
   into STRICT	qt_reg_w 
   from	ordem_compra 
   where	cd_cgc_fornecedor = cd_cgc_fornecedor_p 
   and	cd_estabelecimento = cd_estabelecimento_p 
   and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
elsif (ie_tipo_parametro_p = 'OA') then 
   select	count(*) 
   into STRICT	qt_reg_w 
   from	ordem_compra 
   where	cd_cgc_fornecedor = cd_cgc_fornecedor_p 
   and	cd_estabelecimento = cd_estabelecimento_p 
   and (dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');
elsif (ie_tipo_parametro_p = 'OE') then 
   select	count(*) 
   into STRICT	qt_reg_w 
   from	ordem_compra 
   where	cd_cgc_fornecedor = cd_cgc_fornecedor_p 
   and	cd_estabelecimento = cd_estabelecimento_p 
   and (coalesce(dt_baixa::text, '') = '') 
   and (coalesce(dt_entrega::text, '') = '');
end if;
 
return	qt_reg_w;
 
end;
