CREATE OR REPLACE FUNCTION obter_se_oc_vinculada_nf (nr_ordem_compra_p bigint) RETURNS bigint AS $body$
DECLARE


qt_dados_w			smallint;


BEGIN

select	count(*)
into STRICT	qt_dados_w
from	nota_fiscal a,
	nota_fiscal_item b
where	a.nr_sequencia = b.nr_sequencia
and	b.nr_ordem_compra = nr_ordem_compra_p
and	a.ie_situacao = 1
and	coalesce(a.dt_atualizacao_estoque::text, '') = '';

return	qt_dados_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
