CREATE OR REPLACE FUNCTION obter_se_operacao_terceiro (nr_seq_operacao_p bigint, nr_seq_terceiro_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
count_w			bigint := 0;
count_terceiro_w	bigint := 0;


BEGIN

ds_retorno_w	:= 'S';

if (coalesce(nr_seq_terceiro_p,0) > 0) then

	select	count(*)
	into STRICT	count_w
	from	operacao_terceiro_terc
	where	nr_seq_operacao	= nr_seq_operacao_p;

	if (count_w > 0) then

		select	count(*)
		into STRICT	count_terceiro_w
		from	operacao_terceiro_terc
		where	nr_seq_operacao	= nr_seq_operacao_p
		and	nr_seq_terceiro	= nr_seq_terceiro_p;

		if (count_terceiro_w > 0) then
			ds_retorno_w	:= 'S';
		else
			ds_retorno_w	:= 'N';
		end if;

	else
		ds_retorno_w	:= 'S';
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
