CREATE OR REPLACE FUNCTION obter_se_oper_liberado_usuario ( cd_operacao_estoque_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
qt_existe_w		bigint;
cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
nr_seq_familia_w		bigint;
nr_sequencia_w		bigint;
ie_liberado_w		varchar(1) := 'S';
nm_usuario_lib_w		varchar(15);
cd_perfil_lib_w		bigint;
cd_perfil_ativo_w		bigint := coalesce(obter_perfil_ativo,0);
cd_material_w		integer;
cd_local_estoque_w	smallint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	operacao_estoque_estrut a,
	operacao_estoque_lib b
where	a.nr_sequencia = b.nr_seq_estrut
and	a.cd_operacao_estoque				= cd_operacao_estoque_p
and	coalesce(a.cd_grupo_material,cd_grupo_material_w) 		= cd_grupo_material_w
and	coalesce(a.cd_subgrupo_material,cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(a.cd_classe_material,cd_classe_material_w) 		= cd_classe_material_w
and	coalesce(a.nr_seq_familia,nr_seq_familia_w) 			= nr_seq_familia_w
and	coalesce(a.cd_material,cd_material_w) 			= cd_material_w
order by	coalesce(cd_material,0),
	coalesce(nr_seq_familia,0),
	coalesce(cd_classe_material,0),
	coalesce(cd_subgrupo_material,0),
	coalesce(cd_grupo_material,0);

c02 CURSOR FOR
SELECT	nm_usuario_lib,
	cd_perfil_lib cd_perfil,
	cd_local_estoque,
	coalesce(a.ie_liberado, 'N')
from	operacao_estoque_lib a
where	nr_seq_estrut 			= nr_sequencia_w
and	coalesce(nm_usuario_lib, nm_usuario_p) 	= nm_usuario_p
and	coalesce(cd_perfil_lib, cd_perfil_ativo_w)	= cd_perfil_ativo_w
and	coalesce(cd_local_estoque, cd_local_estoque_p) = cd_local_estoque_p
and	coalesce(cd_perfil_ativo_w,0) > 0

union all

select	nm_usuario_lib,
	0 cd_perfil,
	cd_local_estoque_w,
	coalesce(a.ie_liberado, 'N')
from	operacao_estoque_lib a
where	nr_seq_estrut 			= nr_sequencia_w
and	coalesce(nm_usuario_lib, nm_usuario_p)	= nm_usuario_p
and	coalesce(cd_local_estoque, cd_local_estoque_p) = cd_local_estoque_p
AND	coalesce(cd_perfil_lib::text, '') = ''
and	coalesce(cd_perfil_ativo_w,0) = 0
order by nm_usuario_lib desc,
	 cd_perfil desc,
	 cd_local_estoque desc;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	operacao_estoque_estrut a,
	operacao_estoque_lib b
where	a.nr_sequencia = b.nr_seq_estrut
and	a.cd_operacao_estoque = cd_operacao_estoque_p;

if (qt_existe_w > 0) then
	begin
	cd_material_w	:= coalesce(cd_material_p,0);

	if (cd_material_w = 0) then
		cd_grupo_material_w	:= 0;
		cd_subgrupo_material_w	:= 0;
		cd_classe_material_w	:= 0;
		nr_seq_familia_w	:= 0;
		cd_material_w		:= 0;
	else
		select	cd_grupo_material,
			cd_subgrupo_material,
			cd_classe_material,
			coalesce(nr_seq_familia,0)
		into STRICT	cd_grupo_material_w,
			cd_subgrupo_material_w,
			cd_classe_material_w,
			nr_seq_familia_w
		from	estrutura_material_v
		where	cd_material = cd_material_w;
	end if;

	open c01;
	loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
	close c01;

	if (nr_sequencia_w > 0) then
		open c02;
		loop
		fetch c02 into
			nm_usuario_lib_w,
			cd_perfil_lib_w,
			cd_local_estoque_w,
			ie_liberado_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
		end loop;
		close c02;

		ds_retorno_w := ie_liberado_w;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
