CREATE OR REPLACE FUNCTION obter_se_oper_nf_lib_estab ( cd_operacao_nf_p bigint, cd_estabelebimento_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint;
ie_liberado_w		varchar(1) := 'S';


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	operacao_nota_liberacao
where	cd_operacao_nf = cd_operacao_nf_p;

if (qt_registro_w > 0) then

	select	count(*)
	into STRICT	qt_registro_w
	from	operacao_nota_liberacao
	where	cd_operacao_nf = cd_operacao_nf_p
	and	((cd_estab_lib = cd_estabelebimento_p) or (coalesce(cd_estab_lib::text, '') = ''))
	and	((cd_perfil_lib = cd_perfil_p) or (coalesce(cd_perfil_lib::text, '') = ''));

	if (qt_registro_w = 0) then
		ie_liberado_w := 'N';
	end if;
end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
