CREATE OR REPLACE FUNCTION obter_se_opme_autor_alteracao (nr_seq_autorizacao_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(01) := 'N';

BEGIN
if (coalesce(nr_seq_autorizacao_p,0) > 0) then
	begin
	select	'S'
	into STRICT	ds_retorno_w
	from	material_autor_cirurgia b
	where	b.nr_seq_autorizacao = nr_seq_autorizacao_p
	and	(b.nr_seq_opme IS NOT NULL AND b.nr_seq_opme::text <> '')
	and	exists (SELECT	1
			from	agenda_pac_opme a
			where	a.nr_sequencia = b.nr_seq_opme
			and	exists (select	1
					from	agenda_pac_opme_hist c
					where	c.nr_seq_opme = a.nr_sequencia  LIMIT 1));
	exception
	when others then
		ds_retorno_w := 'N';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
