CREATE OR REPLACE FUNCTION obter_se_opme_planeja_consig (nr_seq_agenda_p bigint, ie_grupo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
qt_resultados_w    	integer;

BEGIN

if (ie_grupo_p IS NOT NULL AND ie_grupo_p::text <> '') then
	select	count(*)
	into STRICT	qt_resultados_w
	from	agenda_pac_opme
	where	nr_seq_agenda	=	nr_seq_agenda_p
	and 	ie_integracao = 'S'
	and (obter_se_contido_char(Obter_estrutura_material(cd_material,'G'),ie_grupo_p) = 'S');

else
	select	count(*)
	into STRICT	qt_resultados_w
	from	agenda_pac_opme
	where	nr_seq_agenda	=	nr_seq_agenda_p
	and 	ie_integracao = 'S';

end if;

if (qt_resultados_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
