CREATE OR REPLACE FUNCTION obter_se_opme_vinculado (nr_seq_proc_interno_p bigint) RETURNS varchar AS $body$
DECLARE


opme_vinculado_w	varchar(2);

BEGIN

select	coalesce(max('S'), 'N')
into STRICT	opme_vinculado_w
from	proc_interno_opme x,
		agenda_paciente a,
		agenda_pac_opme c
where	x.nr_seq_proc_interno = a.nr_seq_proc_interno
and		c.cd_material = x.cd_material
and 	x.nr_seq_proc_interno = nr_seq_proc_interno_p  LIMIT 1;

return opme_vinculado_w;

end;
