CREATE OR REPLACE FUNCTION obter_se_orcamento_pac_atend ( cd_pessoa_fisica_p text, nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
S - Todos
P - convÃªnio particular
*/
ds_retorno_w    varchar(1)     := 'N';
qt_orcamento_w  integer;


BEGIN

select  count(*)
into STRICT    qt_orcamento_w
from    orcamento_paciente
where   nr_atendimento          = nr_atendimento_p
or      cd_pessoa_fisica        = cd_pessoa_fisica_p
and     (((obter_tipo_convenio(cd_convenio) = 1) and (ie_opcao_p = 'P')) or (ie_opcao_p = 'S'));

if (qt_orcamento_w > 0) then
        ds_retorno_w    := 'S';
end if;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
