CREATE OR REPLACE FUNCTION obter_se_ordem_aprovada_conf (nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_aprovados_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_aprovados_w
from	ordem_compra_item
where 	nr_ordem_compra = nr_ordem_compra_p
and	coalesce(dt_lib_conferencia::text, '') = '';

if (qt_aprovados_w > 0) then
	ds_retorno_w := 'N';
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
