CREATE OR REPLACE FUNCTION obter_se_ordem_compra_vencida ( nr_ordem_compra_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(1) := 'N';
qt_registros_w		integer;


BEGIN 
 
select	count(*) 
into STRICT	qt_registros_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p 
and	cd_estabelecimento = cd_estabelecimento_p 
and	(dt_validade IS NOT NULL AND dt_validade::text <> '') 
and	trunc(dt_validade) <= clock_timestamp();
 
if (qt_registros_w > 0) then 
	ds_retorno_w := 'S';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
