CREATE OR REPLACE FUNCTION obter_se_ordem_comp (nr_seq_ordem_p bigint, nr_seq_ordem_comp_p bigint) RETURNS varchar AS $body$
DECLARE


cd_material_w          	integer;
cd_unidade_medida_w    	varchar(5);
qt_dose_w              	double precision;
qt_existe_w		bigint;
ie_existe_w		varchar(1);
nr_seq_ficha_tecnica_w	bigint;
C01 CURSOR FOR
	SELECT	a.cd_material,
		a.qt_dose, /* Menor ou igual */
		a.cd_unidade_medida,
		b.nr_seq_ficha_tecnica
	from	material b,
		can_ordem_item_prescr a
	where	a.cd_material = b.cd_material
	and 	a.nr_seq_ordem = nr_seq_ordem_p;/* Ordem que vou verificar se é compatível */
BEGIN

ie_existe_w := 'S';

open C01;
loop
fetch C01 into
	cd_material_w,
	qt_dose_w,
	cd_unidade_medida_w,
	nr_seq_ficha_tecnica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	/* Ordem que vou manipular */

	select	count(*)
	into STRICT	qt_existe_w
	from	material b,
		can_ordem_item_prescr a
	where	a.nr_seq_ordem = nr_seq_ordem_comp_p
	and 	a.cd_unidade_medida = cd_unidade_medida_w
	and 	((b.nr_seq_ficha_tecnica = nr_seq_ficha_tecnica_w) or (a.cd_material = cd_material_w))
	and 	a.cd_material = b.cd_material
	and	qt_dose_w <= a.qt_dose;

	if (coalesce(qt_existe_w,0) = 0) then
		ie_existe_w := 'N';
	end if;
	end;
end loop;
close C01;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
