CREATE OR REPLACE FUNCTION obter_se_ordem_dispensada ( nr_seq_ordem_p bigint ) RETURNS varchar AS $body$
DECLARE



ie_ordem_ger_w	varchar(1) := 'N';


BEGIN

if (nr_seq_ordem_p > 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_ordem_ger_w
	from	can_ordem_prod
	where	nr_sequencia	=	nr_seq_ordem_p
	and	(dt_entrega_setor IS NOT NULL AND dt_entrega_setor::text <> '');
end if;

return	ie_ordem_ger_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
