CREATE OR REPLACE FUNCTION obter_se_ordem_disp_forn ( nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_valida_w			varchar(01);
ie_visualiza_oc_portal_w		varchar(01);
ie_forma_lib_oc_portal_w		varchar(01);
qt_ordem_w			smallint;
cd_estabelecimento_w		smallint;


BEGIN 
ie_valida_w	:= 'S';
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	ordem_compra 
where	nr_ordem_compra = nr_ordem_compra_p;
 
select	coalesce(max(ie_visualiza_oc_portal), 'S'), 
	coalesce(max(ie_forma_lib_oc_portal), 'A') 
into STRICT	ie_visualiza_oc_portal_w, 
	ie_forma_lib_oc_portal_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_w;
 
if (ie_forma_lib_oc_portal_w = 'A') then 
	select	count(*) 
	into STRICT	qt_ordem_w 
	from	ordem_compra 
	where	nr_ordem_compra = nr_ordem_compra_p 
	and	coalesce(dt_baixa::text, '') = '' 
	and	ie_visualiza_oc_portal_w = 'S' 
	and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');
elsif (ie_forma_lib_oc_portal_w = 'D') then 
	select	count(*) 
	into STRICT	qt_ordem_w 
	from	ordem_compra 
	where	nr_ordem_compra = nr_ordem_compra_p 
	and	coalesce(ie_liberacao_portal, 'N') = 'S' 
	and	coalesce(dt_baixa::text, '') = '' 
	and	ie_visualiza_oc_portal_w = 'S' 
	and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');
end if;
 
 
if (qt_ordem_w = 0) then 
	ie_valida_w	:= 'N';
end if;
 
return ie_valida_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
