CREATE OR REPLACE FUNCTION obter_se_ordem_disp ( nr_prescricao_p bigint, nr_seq_atendimento_p bigint default null) RETURNS varchar AS $body$
DECLARE



ie_ordem_ger_w	varchar(1) := 'N';


BEGIN

if (nr_prescricao_p > 0) then
	select	coalesce(max('S'),'N')
	into STRICT	ie_ordem_ger_w
	from	can_ordem_prod
	where	nr_prescricao	=	nr_prescricao_p
	and	(dt_entrega_setor IS NOT NULL AND dt_entrega_setor::text <> '');

	if (coalesce(ie_ordem_ger_w,'N') = 'N') and (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_ordem_ger_w
		from 	paciente_atendimento
		where	(dt_retirada_medicacao IS NOT NULL AND dt_retirada_medicacao::text <> '')
		and 	nr_seq_atendimento = nr_seq_atendimento_p;
	end if;
end if;

return	ie_ordem_ger_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
