CREATE OR REPLACE FUNCTION obter_se_ordem_inspecionada ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w		integer;
ds_retorno_w		varchar(1);


BEGIN

ds_retorno_w := 'N';

if (nr_item_oci_p > 0) then
	select	count(*)
	into STRICT	qt_reg_w
	from	inspecao_recebimento
	where	nr_ordem_compra = nr_ordem_compra_p
	and	nr_item_oci = nr_item_oci_p;
else
	select	count(*)
	into STRICT	qt_reg_w
	from	inspecao_recebimento
	where	nr_ordem_compra = nr_ordem_compra_p;
end if;

if (qt_reg_w > 0) then
	ds_retorno_w := 'S';
end	if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
