CREATE OR REPLACE FUNCTION obter_se_ordem_oci_insp ( nr_seq_entrega_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w		integer;
ds_retorno_w		varchar(1);


BEGIN

ds_retorno_w := 'N';

select	count(*)
into STRICT	qt_reg_w
from	inspecao_recebimento
where	nr_seq_entrega = nr_seq_entrega_p;

if (qt_reg_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end	;
$body$
