CREATE OR REPLACE FUNCTION obter_se_ordem_reaproveitada (nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE


ie_ordem_reaproveitada_w	varchar(1);


BEGIN

if (coalesce(nr_seq_ordem_p,0) > 0) then
	select 	coalesce(max('S'),'N')
	into STRICT	ie_ordem_reaproveitada_w
	from 	Can_ordem_prod_mat
	where	nr_seq_ordem_origem = nr_seq_ordem_p;
end if;

return	ie_ordem_reaproveitada_w;

end;
$body$
LANGUAGE PLPGSQL
